mapper.select:
SELECT IFNULL(SUM(amount),0.00)
FROM j_bonus_cj


		and date_format(create_time, '%Y-%m-%d') >= #{beginTime}


		and date_format(create_time, '%Y-%m-%d') <= #{endTime}

	AND status = 1

SELECT IFNULL(SUM(amount),0.00) FROM j_bonus_cj
and date_format(create_time, '%Y-%m') = #{month}
AND status = 1
