mapper.select:
select a.id,a.u_id,e.real_name u_name,a.a_id,d.real_name a_name,a.a_pv,a.b_id,f.real_name b_name,a.b_pv,a.pv, DATE_FORMAT(a.created, '%Y-%m-%d') created from j_bonus_scj a left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) d on a.a_id = d.id left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) e on a.u_id = e.id left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) f on a.b_id = f.id
and a.u_id = #{uId,jdbcType=INTEGER}
and date_format(a.created, '%Y-%m-%d') >= #{beginTime}
and date_format(a.created, '%Y-%m-%d') <= #{endTime}
and a.status = 1
ORDER BY a.created DESC

select a.u_id,e.real_name u_name,a.a_id,d.real_name a_name,IFNULL(SUM(a.a_pv),0.00) as a_pv,a.b_id, f.real_name b_name,IFNULL(SUM(a.b_pv),0.00) as b_pv,IFNULL(SUM(a.pv),0.00) as pv from j_bonus_scj a left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) d on a.a_id = d.id left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) e on a.u_id = e.id left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) f on a.b_id = f.id
and date_format(a.created, '%Y-%m-%d') >= #{beginTime}
and date_format(a.created, '%Y-%m-%d') <= #{endTime}
and a.status = 1
group by a.u_id

select IFNULL(SUM(pv),0.00) as pv
from j_bonus_scj

	
		and date_format(created, '%Y-%m-%d') >= #{beginTime}
	
	
		and date_format(created, '%Y-%m-%d') <= #{endTime}
	
	and status = 1

select a.id,a.u_id,e.real_name u_name,a.a_id,d.real_name a_name,a.a_pv,a.b_id,f.real_name b_name,a.b_pv,a.pv, DATE_FORMAT(a.created, '%Y-%m-%d') created from j_bonus_scj a left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) d on a.a_id = d.id left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) e on a.u_id = e.id left join (select b.id,c.real_name from u_user b,u_user_auth c where b.uuid = c.u_uuid ) f on a.b_id = f.id
and a.u_id = #{uId,jdbcType=INTEGER}
and date_format(a.created, '%Y-%m') = #{month}
and a.status = 1
ORDER BY created DESC

select IFNULL(SUM(pv),0.00) as pv from j_bonus_scj
and date_format(created, '%Y-%m') = #{month}
and status = 1
