mapper.select:
select id,u_id,a_id,a_pv,b_id,b_pv,pv,DATE_FORMAT(created, '%Y-%m-%d') created
from j_bonus_scj

	
		and u_id = #{uId,jdbcType=INTEGER}
	
	
		and date_format(created, '%Y-%m-%d') >= #{beginTime}
	
	
		and date_format(created, '%Y-%m-%d') <= #{endTime}
	
	and status = 1

ORDER BY created DESC

select u_id,a_id,IFNULL(SUM(a_pv),0.00) as a_pv,b_id,IFNULL(SUM(b_pv),0.00) as b_pv,IFNULL(SUM(pv),0.00) as pv
from j_bonus_scj

	
		and date_format(created, '%Y-%m-%d') >= #{beginTime}
	
	
		and date_format(created, '%Y-%m-%d') <= #{endTime}
	
	and status = 1

group by u_id

select IFNULL(SUM(pv),0.00) as pv
from j_bonus_scj

	
		and date_format(created, '%Y-%m-%d') >= #{beginTime}
	
	
		and date_format(created, '%Y-%m-%d') <= #{endTime}
	
	and status = 1

select id,u_id,a_id,a_pv,b_id,b_pv,pv,DATE_FORMAT(created, '%Y-%m-%d') created
from j_bonus_scj
 
	
		and u_id = #{uId,jdbcType=INTEGER}
	
	
		and date_format(created, '%Y-%m') = #{month}
	
	and status = 1

ORDER BY  created DESC

select IFNULL(SUM(pv),0.00) as pv from j_bonus_scj
and date_format(created, '%Y-%m') = #{month}
and status = 1
