mapper.update:
update p_news
   set titles = #{titles},pics = #{pics},contents = #{contents},type = #{type},last_modify_time = #{lastModifyTime},byte_size = #{byteSize}
where id=#{id}

mapper.select:
select * from ( select s.id as sid,s.d_id,s.p_id,s.doc_id,s.news_id,DATE_FORMAT(s.created_at, '%Y-%m-%d %H:%i:%s' ) fxsj,s.type as ys_type,s.is_read,d.account,d.uname,d.dw_name,d.avatar,n.* from p_share s left join p_account_doctors d on s.doc_id = d.id left join p_news n on s.news_id = n.id) t
t.ys_type=2 and t.doc_id =#{dId}
order by t.fxsj desc limit 6

select * from ( select s.id as sid,s.d_id,s.p_id,s.doc_id,s.news_id,s.created_at as sjc,DATE_FORMAT(s.created_at, '%Y-%m-%d %H:%i:%s' ) fxsj,s.type as ys_type,s.is_read,d.account,d.uname,d.dw_name,d.avatar,n.* from p_share s left join p_account_doctors d on s.doc_id = d.id left join p_news n on s.news_id = n.id) t
t.ys_type=2 and t.doc_id = #{dId} and t.id > #{id}
order by t.fxsj limit 6

select * from ( select s.id as sid,s.d_id,s.p_id,s.doc_id,s.news_id,s.created_at as sjc,DATE_FORMAT(s.created_at, '%Y-%m-%d %H:%i:%s' ) fxsj,s.type as ys_type,s.is_read,d.account,d.uname,d.dw_name,d.avatar,n.* from p_share s left join p_account_doctors d on s.doc_id = d.id left join p_news n on s.news_id = n.id) t
t.ys_type=2 and t.doc_id = #{dId} and t.id < #{id}
order by t.fxsj desc limit 6

mapper.update:
update p_share
   set is_read = 1
where id=#{sid}
