mapper.select:
select t.*,DATE_FORMAT(t.created_at, '%Y-%m-%d %H:%i' ) cjsj from (select u.*,c.bz_value as title from u_shopping_score_balance u left join p_xt_cl c on u.category = c.bz_id where c.bz_type='category_type') t
1 = 1 and t.u_uuid = #{uUuid}
order by created_at desc

select m.*, DATE_FORMAT(m.created_at, '%Y-%m-%d %H:%i' ) cjsj from (select u.*,c.bz_value as title from u_cash_score_balance u left join p_xt_cl c on u.category = c.bz_id where c.bz_type='category_type') m
1 = 1 and m.u_uuid = #{uUuid}
order by created_at desc

select IFNULL(sum(score),0.00) from u_cash_score_balance 

	 and category = {category}
	and type = 1 and status = 1 and end = 2 and to_days(now()) - to_days(created_at) = 1

select IFNULL(sum(score),0.00) from u_cash_score_balance 

	 and category = {category}
	and type = 1 and status = 1 and end = 2 and YEARWEEK(date_format(created_at,'%Y-%m-%d')) = YEARWEEK(now())

  		select IFNULL(sum(score),0.00) from u_cash_score_balance 
  		
  			 and category = {category}
  			
				and date_format(created_at, '%Y-%m-%d') >= #{beginTime}
			
			
				and date_format(created_at, '%Y-%m-%d') <= #{endTime}
			
  			and type = 1 and status = 1 and end = 2

  		select IFNULL(sum(score),0.00) from u_cash_score_balance 
  		
  			 and category = {category}
  			
				and date_format(created_at, '%Y-%m') = #{month}
			
  			and type = 1 and status = 1 and end = 2

select IFNULL(sum(score),0.00) from u_shopping_score_balance 

	 and category = {category}
	and type = 1 and status = 1 and end = 2 and to_days(now()) - to_days(created_at) = 1

select IFNULL(sum(score),0.00) from u_shopping_score_balance 

	 and category = {category}
	and type = 1 and status = 1 and end = 2 and YEARWEEK(date_format(created_at,'%Y-%m-%d')) = YEARWEEK(now())

  		select IFNULL(sum(score),0.00) from u_shopping_score_balance 
  		
  			 and category = {category}
  			
				and date_format(created_at, '%Y-%m-%d') >= #{beginTime}
			
			
				and date_format(created_at, '%Y-%m-%d') <= #{endTime}
			
  			and type = 1 and status = 1 and end = 2

  		select IFNULL(sum(score),0.00) from u_shopping_score_balance 
  		
  			 and category = {category}
  			
				and date_format(created_at, '%Y-%m') = #{month}
			
  			and type = 1 and status = 1 and end = 2
